import React from 'react';
import { useForm } from 'react-hook-form';

const planos = [
  {
    id: 1,
    value: 'FaleMais 30',
  },
  {
    id: 2,
    value: 'FaleMais 60',
  },
  {
    id: 3,
    value: 'FaleMais 120',
  },
  {
    id: 4,
    value: 'Sem Plano'
  }
];

function Calculator() {
    const {register, handleSubmit, errors} = useForm();

    const onSubmit = (data) => {
      console.log(data);
    };

  return (
    <div className="calculator">
      <h1 style={{ textAlign: 'center' }}>Calcule o valor de suas chamadas com ou sem plano!</h1>
        <form onSubmit={handleSubmit(onSubmit)}>
          <select value={this.state.value} onChange={handleChange}>
            <option value="laranja">Laranja</option>
            <option value="limao">Lim√£o</option>
            <option value="coco">Coco</option>
            <option value="manga">Manga</option>
          </select>
          <input type="number" placeholder="Quantos minutos quer falar?" className="input__tempo" ref={register({required:true})}/>
          {errors.input__tempo && <p>O campo minutos precisa ser preenchido</p>}

          <input type="submit" className="submit__calc" ref={register}/>
        </form>
    </div>
  )
}

export default Calculator
