import { useForm } from 'react-hook-form';
import './Calculator.css';

const planos = [
  {
    id: 1,
    value: 'FaleMais 30',
  },
  {
    id: 2,
    value: 'FaleMais 60',
  },
  {
    id: 3,
    value: 'FaleMais 120',
  },
  {
    id: 4,
    value: 'Sem Plano'
  }
];

function Calculator() {
    const {register, handleSubmit, formState: { errors } } = useForm();

