      <div>
        <h1 style={{ textAlign: 'center' }}>Calcule o valor de suas chamadas com ou sem plano!</h1>
        <div className="calculator__line"></div>
        <main>
          <div className="calculator__body">
            <form>

              <div className="fields">
                <label>Minutos: </label>
                <input type="text" placeholder="Quantos minutos quer falar?" className="input__minutos"/>
              </div>

              <div className="calculator__btn">
                <select className="select__plano"/>
                
              </div>

              <div className="calculator__btn">
                <button type="submit" className="submit__calc"/>
              </div>

            </form>
            </div>
          </main>
      </div>
  )
}

export default Calculator
