import React from 'react';
import { useForm } from 'react-hook-form';
import './Calculator.css';

const planos = [
  {
    id: 1,
    value: 'FaleMais 30',
  },
  {
    id: 2,
    value: 'FaleMais 60',
  },
  {
    id: 3,
    value: 'FaleMais 120',
  },
  {
    id: 4,
    value: 'Sem Plano'
  }
];

function Calculator() {
    const {register, handleSubmit, formState: { errors } } = useForm();

    const onSubmit = (data) => {
      console.log(data);
    };

  return (
      <div>
        <h1 style={{ textAlign: 'center' }}>Calcule o valor de suas chamadas com ou sem plano!</h1>
        <div className="calculator__line"></div>
        <main>
          <div className="calculator__body">
            <form>

              <div div className="fields">
                <label>Selecione seu Plano: </label>
                <select className="input__planos">
                  <option id='1' value='FaleMais 30'>FaleMais 30</option>
                  <option id='2' value='FaleMais 60'>FaleMais 60</option>
                  <option id='3' value='FaleMais 120'>FaleMais 120</option>
                </select>
              </div>

              <div div className="fields">
                <label>Selecione a Origem da Ligação: </label>
                <select className="input__origem">
                  <option value='011'>DDD 011</option>
                  <option value='016'>Interior São Paulo (016)</option>
                  <option value='017'>São Paulo (017)</option>
                  <option value='018'>São Paulo (011)</option>
                </select>
              </div>

              <div className="fields">
                <label>Minutos: </label>
                <input type="text" placeholder="Quantos minutos quer falar?" className="input__minutos"/>
              </div>

              <div className="calculator__btn">
                <button type="submit" className="submit__calc"/>
              </div>

            </form>
            </div>
          </main>
      </div>
  )
}

export default Calculator
