import React from 'react';
import { useForm } from 'react-hook-form';

const planos = [
  {
    id: 1,
    value: 'FaleMais 30',
  },
  {
    id: 2,
    value: 'FaleMais 60',
  },
  {
    id: 3,
    value: 'FaleMais 120',
  },
  {
    id: 4,
    value: 'Sem Plano'
  }
];

function Calculator() {
    const {register, handleSubmit, formState: { errors } } = useForm();

    const onSubmit = (data) => {
      console.log(data);
    };

  return (
      <>
        <header style={{ textAlign: 'center' }}>Calcule o valor de suas chamadas com ou sem plano!</header>
        <main>
          <div className="calculator__body">
            <form onSubmit={handleSubmit(onSubmit)}>
              <div className="fields">
                <select value={planos}>
                </select>
                <input type="number" placeholder="Quantos minutos quer falar?" className="input__tempo" ref={register({required:true})}/>
                {errors.input__tempo && <p>O campo minutos precisa ser preenchido</p>}
                <input type="submit" className="submit__calc" ref={register}/>
              </div>
            </form>
            </div>
          </main>
      </>
  )
}

export default Calculator
