import React from 'react';
import { useForm } from 'react-hook-form';
import Dropdown from '../components/Dropdown.js';

const planos = [
  {
    id: 1,
    value: 'FaleMais 30',
  },
  {
    id: 2,
    value: 'FaleMais 60',
  },
  {
    id: 3,
    value: 'FaleMais 120',
  },
  {
    id: 4,
    value: 'Sem Plano'
  }
];

function Calculator() {
    const {register, handleSubmit, errors} = useForm();

    const onSubmit = (data) => {
      console.log(data);
    };

  return (
    <div className="div calculator">
      <h1>
          Calcule o valor das suas chamadas!
      </h1>
      <div className="calculator__container">
      <div className="calculator__wraper">
        <ul className="calculator__items">
          <li className="calculator__item">
            <Dropdown title="Escolha um Plano" items={planos}/>
          </li>
          <li className="calculator__item">
            <Dropdown title="Escolha a Origem da Ligação"/>
          </li>
          <li className="calculator__item">
            <Dropdown title="Escolha o Destino da Ligação" />
          </li>
            {/* <h1 style={{ textAlign: 'center' }}>Calcule o valor de suas chamadas com ou sem plano!</h1>
              <form onSubmit={handleSubmit(onSubmit)}>
                
                
                <input type="number" placeholder="Quantos minutos quer falar?" className="input__tempo" ref={register({required:true})}/>
                {errors.input__tempo && <p>O campo minutos precisa ser preenchido</p>}

                <input type="submit" className="submit__calc" ref={register}/>
              </form> */}
          </ul>
      </div>
  </div>
</div>

  )
}

export default Calculator
