import React, {useState} from 'react'
import { Link } from 'react-router-dom'
import { Button } from './Button';
import './Navbar.css';

function Navbar() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(false);
        } else {
            setButton(true);
        }
    };

    window.addEventListener('resize', showButton);

    return (
        <div>
        <>
        <nav className="navbar">
            <div className="navbar-container">
                <Link to="/" className="navbar-logo">
                    VxTel <i className="fab fa-typo3"></i>
                </Link>
                <div className="menu-icon" onClick={handleClick}>
                    <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                </div>
                <ul className={click ?'nav-menu active' : 'nav-menu'}>
                    <li className="home">
                        <Link to='/' className="nav-links" onClick={closeMobileMenu}>
                            Principal
                        </Link>
                    </li>
                    <li className="services">
                        <Link to='/servicos' className="nav-links" onClick={closeMobileMenu}>
                            Serviços
                        </Link>
                    </li>
                    <li className="products">
                        <Link to='/produtos' className="nav-links" onClick={closeMobileMenu}>
                            Produtos
                        </Link>
                    </li> 
                    <li className="">
                        <Link to='/assine' className="nav-links-mobile" onClick={closeMobileMenu}>
                            Assine Já!
                        </Link>
                    </li>     
                </ul>
                {button && <Button buttonstyle='btn--outline'>ASSINE</Button>}
            </div>
        </nav>
        </>
        </div>
    )
}

export default Navbar;
